import matplotlib.pyplot as pyplot

def plot_graph( index_list, 
                distribution, 
                distribution_label,
                title, 
                xlabel, 
                ylabel, 
                filename):
    # graph result
    x = index_list


    y1 = distribution
    # pyplot.plot(x, y1, color="blue", label=distribution_label, marker="o")
    # pyplot.plot(x, y1, "ro", color="blue", label=distribution_label)
    pyplot.bar(x, y1, color="blue", label=distribution_label)


    pyplot.xlabel(xlabel)
    pyplot.ylabel(ylabel)
    pyplot.title(title)

    pyplot.legend()
    pyplot.savefig(filename)
    pyplot.close()

def analyse_distribution(distribution):
    d = dict()
    for ocurrence in distribution:
        if ocurrence not in d:
            d[ocurrence] = 1
        else:
            d[ocurrence] = d[ocurrence] + 1
    
    occurrence_list = []
    for each in d.keys():
        occurrence_list.append(0)

    for each in d.keys():
        occurrence_list.insert(each, d[each])
    
    return occurrence_list


